import { apiSlice } from './apiSlice';
import {  chat_URL } from '../constants';


export const chatApiSlice = apiSlice.injectEndpoints({
    endpoints: (builder) => ({
        getChat: builder.mutation({
            query: (question) => ({
                url: chat_URL,
                method: 'POST',
                body: { ...question },
            }),
        }),
    }),
});


export const { useGetChatMutation } = chatApiSlice;
